export default function rootReducer(state = { user: undefined, activityList: [] }, action){
	switch(action.type){
		case 'CONNECT_USER':
			if(action.user!==undefined){
				return { ...state, user: action.user }
			}else{
				return { ...state, user: action.user, activityList: [] }
			}	
		default:
			return state
    }
}
