import React from 'react';
import { connect } from "react-redux";
import { getUserActivities } from '../actions/actions'
import { User } from '../user'
const axios = require('axios');

const mapStateToProps = (state) => {
    return {
      activities: state.activityList
    }
}

const mapDispatchToProps = dispatch => {
    return {
        getUserActivities: activities => dispatch(getUserActivities(activities))
    };
  };

class ActivityList extends React.Component {
    constructor(props) {
      super(props);
      this.state = { activities: undefined };
    }

    getListActivities = () => {
        let url = 'http://localhost:8080/user/getUser/5';
        let res = axios.get(url).then(response => this.setState({activities: response.data}));
        console.log(res);
    }

    renderListActivities = () => {
        this.getListActivities()
        let Data = this.state.activities, MakeItem = function(X) {
            return <li key={X.name}>{X.name}</li>
        };
        return Data.map(MakeItem);
    }

    render() {
        return <ul>{this.renderListActivities()}</ul>
    }
}

const ConnectedActivityList = connect(mapStateToProps, mapDispatchToProps)(ActivityList);

export {
    ConnectedActivityList
}
