import React from 'react';
import { connect } from "react-redux";
import { getUserActivities } from '../actions/actions'
import { User } from '../user'
const axios = require('axios');

const mapStateToProps = (state) => {
    return {
      activities: state.activityList
    }
}

const mapDispatchToProps = dispatch => {
    return {
        getUserActivities: activities => dispatch(getUserActivities(activities))
    };
  };

class ActivityList extends React.Component {
    constructor(props) {
      super(props);
      this.state = { activities: [] };
    }

    getListActivities = () => {
        let url = 'http://localhost:8080/user/getUser/5';
        let res = axios.get(url).then(response => this.setState({activities: response.data.activities.name}));
        //this.props.getUserActivities(/*TABLEAU a partir de res*/);
        console.log(res);
    }

    testplz = () => {
        console.log(this.state.activities);
    }

    renderListActivities = () => {
        return <div>
            <button onClick={this.testplz.bind(this)}>PROMISE</button>
            <button onClick={this.getListActivities.bind(this)}>display activities</button></div>
    }

    render() {
        return <ul>{this.renderListActivities()}</ul>
    }
}

const ConnectedActivityList = connect(mapStateToProps, mapDispatchToProps)(ActivityList);

export {
    ConnectedActivityList
}
