import React from 'react';
import { connect } from "react-redux";
import { getUserActivities } from '../actions/actions'
const axios = require('axios');

const mapStateToProps = (state) => {
    return {
      activities: state.activityList
    }
}

const mapDispatchToProps = dispatch => {
    return {
        getUserActivities: activities => dispatch(getUserActivities(activities))
    };
  };

class ActivityList extends React.Component {

    getListActivities = () => {
        let url = 'http://localhost:8080/user/getUser/5';//CHANGER l'ID EN FONCTION DU USER QUAND LE BACKEND SERA OK
        axios.get(url).then(response => this.props.getUserActivities(response.data.activities));
    }

    displayListActivities = () => {
        var Data = this.props.activities, MakeItem = function(X) {
              return <li key={X.name}>{X.name}</li>
        };
        return Data.map(MakeItem);
    }

    render() {
        return <div>
            <ul>{this.displayListActivities()}</ul>
            <button onClick={this.getListActivities.bind(this)}>display activities</button>
        </div>
    }
}

const ConnectedActivityList = connect(mapStateToProps, mapDispatchToProps)(ActivityList);

export {
    ConnectedActivityList
}
