import React from 'react';
import { connect } from "react-redux";
import { getUserRegions } from '../actions/actions'
const axios = require('axios');

class RegionList extends React.Component {

    getListRegions = () => {
        let url = 'http://localhost:8080/user/getUser/5';//CHANGER l'ID EN FONCTION DU USER QUAND LE BACKEND SERA OK
        axios.get(url).then(response => this.props.getUserRegions(response.data.regions));
    }

    displayListRegions = () => {
        var Data = this.props.regions, MakeItem = function(X) {
            return <li key={X.city}>{X.city}</li>//
        };
        return Data.map(MakeItem);
    }

    render() {
        return <div>
            <ul>{this.displayListRegions()}</ul>
            <button onClick={this.getListRegions.bind(this)}>display regions</button>
        </div>
    }
}

const ConnectedRegionList = connect(mapStateToProps, mapDispatchToProps)(RegionList);

export {
    ConnectedRegionList
}
